    let azimuthindex
    let azimuthSpectemp = []
    for (var i = 0; i < self.ctx.data.length; i++) {
        var curData = self.ctx.data[i];
        if (curData.data[0]) {
            console.log('curData.data[0]',curData.data[0])
            if (0<curData.data[0][1]&&curData.data[0][1]<360)
            {

                let azimuth=Math.trunc(curData.data[0][1]).toString();

                if (azimuth.substr(0, azimuth.length - 1) == "") {
                    azimuthindex = 0
                } else {
                    azimuthindex = azimuth.substr(0, azimuth.length - 1)
                }

                for (let i = 0; i < 36; i++) {
                    if (i == azimuthindex) {
                        azimuthSpectemp.push([6, i, 1])
                    } else {
                        azimuthSpectemp.push([6, i, 0])
                    }
                }



            }
        }
    }

    var mychart = self.ctx.myChart;
    var option = mychart.getOption();
     mychart.setOption({
        series:{
            data: azimuthSpectemp
        }
    })

}

self.onResize = function() {
  self.ctx.myChart.resize({
        width: self.ctx.width,
        height: self.ctx.height,
    })
}

