var dashboard = null;

self.onInit = function() {

    var dom = self.ctx.$container;
    dom.width(self.ctx.width);
    dom.height(self.ctx.height);
    // NOTE: Assuming predefined number of stacked channels.
    const SIGNALS = new Array(6).fill(0).map((_, i) =>
        ({
            title: `声发射${i + 1}`,
        }))
    const DEFAULT_X_RANGE_MS = 30 * 1000
    const PADDING_BOTTOM = 30
    const PADDING_TOP = 40
    const PADDING_LEFT = 100
    const PADDING_AXIS_Y = 6

    const {
        lightningChart,
        AutoCursorModes,
        emptyLine,
        AxisTickStrategies,
        AxisScrollStrategies,
        synchronizeAxisIntervals,
        UIOrigins,
        UIDraggingModes,
        LegendBoxBuilders,
        Themes,
        FontSettings,
    } = lcjs

    const exampleContainer = document.getElementById(
        dom[0]) || document.body

    dashboard = lightningChart()
        .Dashboard({
            numberOfColumns: 1,
            numberOfRows: SIGNALS.length,
            container: dom[0],
            theme: Themes.darkGold,
        })
        .setSplitterStyle(emptyLine)

    const layoutDashboard = () => {
        const totalHeight = exampleContainer
            .getBoundingClientRect().height
        const signalHeight = (totalHeight -
                PADDING_BOTTOM - PADDING_AXIS_Y * (
                    SIGNALS.length - 1)) /
            SIGNALS.length

        SIGNALS.forEach((_, iSignal) => {
            const chHeight = signalHeight +
                (
                    iSignal === SIGNALS
                    .length - 1 ?
                    PADDING_BOTTOM :
                    PADDING_AXIS_Y)
            dashboard.setRowHeight(iSignal,
                chHeight)
        })
    }
    requestAnimationFrame(layoutDashboard)

    const channels = SIGNALS.map((signal, iSignal) => {
        const chart = dashboard
            .createChartXY({
                columnIndex: 0,
                rowIndex: iSignal,
            })
            .setTitle('')
            .setPadding({
                top: 0,
                bottom: iSignal < SIGNALS
                    .length - 1 ?
                    PADDING_AXIS_Y : 0,
                left: 0,
            })
            .setAutoCursorMode(AutoCursorModes
                .disabled)
            .setBackgroundStrokeStyle(emptyLine)
            .setMouseInteractions(false)

        const axisX = chart
            .getDefaultAxisX()
            .setTickStrategy(AxisTickStrategies
                .Empty)

        const axisY = chart
            .getDefaultAxisY()
            .setTickStrategy(AxisTickStrategies
                .Empty)
            .setStrokeStyle(emptyLine)
            .setTitle(signal.title)
            .setTitleRotation(0)
            .setThickness(60)

        axisY.setTitleFont(new FontSettings({
            family: 'Arial'
        }))

        const series = chart
            .addLineSeries({
                dataPattern: {
                    pattern: 'ProgressiveX'
                },
                automaticColorIndex: iSignal,
            })
            .setName(`Channel ${iSignal + 1}`)
            // Use 2 thickness for smooth anti-aliased thick lines with the best visual look, this is pretty GPU heavy.
            .setStrokeStyle((style) => style
                .setThickness(2))
        return {
            chart,
            series,
            axisX,
            axisY
        }
    })

    const channelBottom = channels[channels.length - 1]

    const axisX = channelBottom.axisX
        .setThickness(PADDING_BOTTOM).setTickStrategy(
            AxisTickStrategies.Numeric
        )
    synchronizeAxisIntervals(...channels.map((ch) => ch
        .axisX))
    self.ctx.channels = channels
    self.ctx.temp = [
        [],
        [],
        [],
        [],
        [],
        [],
    ]
    self.ctx.time = 0
    for (var i = 0; i < self.ctx.data.length; i++) {
        var datasourceData = self.ctx.data[i];
        var dataSet = datasourceData.data;
        var data = []
        for (var d = 0; d < dataSet.length; d++) {
            var tsValuePair = dataSet[d];
            var ts = tsValuePair[0];
            self.ctx.temp[i] = ts
        }
    }


}
self.onDataUpdated = function() {
    var channels = self.ctx.channels
    self.ctx.time = self.ctx.time + 1
    for (var i = 0; i < self.ctx.data.length; i++) {
        var datasourceData = self.ctx.data[i];
        var dataSet = datasourceData.data;
        var data = []
        for (var d = 0; d < dataSet.length; d++) {
            var tsValuePair = dataSet[d];
            var ts = tsValuePair[0];

            if (tsValuePair[1] != '') {
                var value = JSON.parse(tsValuePair[
                    1]);
                var wavedata = value["wave"]

                if (self.ctx.time > 10) {
                    if (self.ctx.temp[i] == ts) {
                        channels[i].series.clear()
                    } else {

                        for (var j = 0; j < wavedata
                            .length; j++) {
                            data.push({
                                x: j,
                                y: wavedata[j]
                            })
                        }
                        channels[i].series.clear().add(
                            data)
                    }
                }
            }
        }
    }
}

self.onDestroy = function() {
    console.log('dashboard dispose')
    dashboard.dispose();
}

